import React,{Component} from 'react';
import { Modal,Effect} from 'react-dynamic-modal';
import SquareButton from '../CustomButtons/SquareButton';
import Aux from '../hoc/AuxComponent';

import ListItem from "../ListComponent/ListItem/ListItem";

class MyModal extends Component{
   render(){
      const { params,counts, onRequestClose } = this.props;
      //console.log("params: "+params)
      let index_pom=99;
      let vypis_pom=null;
      return (
         <Modal
            onRequestClose={onRequestClose}
            effect={Effect.SlideFromBottom}>

               { params.map((item,index)=>{
                  console.log("======== mapujem skrz itemy")
                  console.log("pom index: "+index_pom)
                  console.log("item: "+item.question)
                  console.log(index_pom===item.question)
                  if(index_pom===item.question){
                     //console.log("true: "+index_pom)
                     return (
                           <SquareButton key={index}>{item.answer } </SquareButton>
                      )
                  }else{
                     //console.log("false: "+index_pom)
                     index_pom=item.question;
                     return (
                        <Aux key={index} > 
                           <br></br>
                           <h3>{ item.wording } </h3>
                           <SquareButton  >{item.answer } </SquareButton>
                        </Aux>
                      )
                  }
                  //index_pom=item[0];


               }) }

         </Modal>
      );
   }
}

export default MyModal;
