import React,{Component} from 'react';
import { Modal,Effect} from 'react-dynamic-modal';
import SquareButton from '../CustomButtons/SquareButton';
import { makeStyles } from "@material-ui/core/styles";
import Aux from '../hoc/AuxComponent';
import './Modal.css';

const styles = {
   overlay: {
      position        : 'relative',
      top             : 0,
      left            : 0,
      right           : 0,
      bottom          : 0,
      zIndex          : 99999999,
      overflowY       : 'scroll',
      height          : '500px',
      backgroundColor : 'rgba(0, 0, 0, 0.3)'
    },

    content: {
      position                : 'relative',
      margin                  : '15% auto',
      width                   : '50%',
      border                  : '1px solid rgba(0, 0, 0, .2)',
      background              : '#fff',
      overflowY               : 'scroll',
      borderRadius            : '4px',
      outline                 : 'none',
      boxShadow               : '0 5px 10px rgba(0, 0, 0, .3)',

    }
 };


class MyModal extends Component{
   constructor(props) {
      super(props);

      this.state = {
        classes : makeStyles(styles),
        hlavicka:[],
        odpovede:[],
        parametre:[],
        onRequestClose:null
      }
    }

    componentDidMount(){
      //const { hlavicka,odpovede,parametre, onRequestClose } = this.props;
      this.setState({ 
         hlavicka:this.props.hlavicka,
        odpovede:this.props.odpovede,
        parametre:this.props.parametre,
        onRequestClose:this.props.onRequestClose
       });
    }

    handleClick=()=>{
      //this.state.onRequestClose;
    }

   render(){



      let index_pom=99;

      if(this.state.parametre[0].accept_answ===0){

         return (
            <Modal
               onRequestClose={this.state.onRequestClose}
               effect={Effect.SlideFromBottom}
               className={this.state.classes.content,this.state.classes.overlay}
               >
                  <h3 className='h3-custom'>{ this.state.hlavicka[0].name } </h3>
                  <p className='h5-custom'>{ this.state.hlavicka[0].wording } </p>

               {this.state.odpovede.map((item,index)=>{
                  return <SquareButton key={index} clicked={this.handleClick}>{item.answer } </SquareButton>
               }
               )} 
            </Modal>
         );
      }else{
         return(
            <Modal
               onRequestClose={this.state.onRequestClose}
               effect={Effect.SlideFromBottom}
               className={this.state.classes.content,this.state.classes.overlay}
               >
                  <h3 className='h3-custom'>{ this.state.hlavicka[0].name } </h3>
                  <p className='h5-custom'>{ this.state.hlavicka[0].wording } </p>

            </Modal>
         )
      }

   }
}

export default MyModal;
