import React,{Component} from 'react';
import { Modal,Effect} from 'react-dynamic-modal';
import SquareButton from '../CustomButtons/SquareButton';
import { makeStyles } from "@material-ui/core/styles";
import Aux from '../hoc/AuxComponent';
import './Modal.css';

const styles = {
   overlay: {
      position        : 'fixed',
      top             : 0,
      left            : 0,
      right           : 0,
      bottom          : 0,
      zIndex          : 99999999,
      overflow        : 'hidden',
      perspective     :  1300,
      backgroundColor : 'rgba(0, 0, 0, 0.3)'
    },

    content: {
      position                : 'relative',
      margin                  : '15% auto',
      width                   : '50%',
      border                  : '1px solid rgba(0, 0, 0, .2)',
      background              : '#fff',
      overflowY                : 'auto',
      borderRadius            : '4px',
      outline                 : 'none',
      boxShadow               : '0 5px 10px rgba(0, 0, 0, .3)',

      maxHeight: '30px',
      boxSizing: 'border-box',
      transition: 'all 0.3s ease-out',
    }
 };


class MyModal extends Component{
   constructor(props) {
      super(props);

      this.state = {
        classes : makeStyles(styles)
      }
    }

   render(){
      const { params,counts, onRequestClose } = this.props;
      //console.log("params: "+params)
      let index_pom=99;
      let vypis_pom=null;
      return (
         <Modal
            onRequestClose={onRequestClose}
            effect={Effect.SlideFromBottom}
            className={this.state.classes.content}
            >

               { params.map((item,index)=>{
                  //console.log("======== mapujem skrz itemy")
                  //console.log("pom index: "+index_pom)
                  //console.log("item: "+item.question)
                  //console.log(index_pom===item.question)
                  if(index_pom===item.question){
                     //console.log("true: "+index_pom)
                     return (
                           <SquareButton key={index}>{item.answer } </SquareButton>
                      )
                  }else{
                     //console.log("false: "+index_pom)
                     index_pom=item.question;
                     return (
                        <Aux key={index} > 
                           <h3 className='h3-custom'>{ item.wording } </h3>
                           <SquareButton  >{item.answer } </SquareButton>
                        </Aux>
                      )
                  }
                  //index_pom=item[0];


               }) }

         </Modal>
      );
   }
}

export default MyModal;
