import React, { Component }  from 'react';
import Axios from 'axios'
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';

import {Redirect } from "react-router-dom";
import logo from 'assets/img/raise-hand-blue.png';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://skauting.sk/">
        SlovenskÃ½ skauting
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const styles = {
  paper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    //margin: theme.spacing(1),
    //backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    //marginTop: theme.spacing(1),
  },
  submit: {
    //margin: theme.spacing(3, 0, 2),
    backgroundColor: "#019ECE",
  }
};


class LoginClass extends Component{
  constructor(props) {

    super(props);
    this.state = {
      classes : makeStyles(styles),
      error: null,
      islogged: false,
      loginParams:{
        login: '',
        password: ''
      }
    };
  }

  handleFormChange = event => {
    let loginParamsNew = { ...this.state.loginParams };
    let val = event.target.value;
    loginParamsNew[event.target.name] = val;
    this.setState({
      loginParams: loginParamsNew
    });
  };

  login = event => {
    this.setState({ error: '' });
    Axios.post('http://localhost:4001/user/login', this.state.loginParams)
      .then(res => {
        console.log('success login', res.data)

        localStorage.setItem("token", res.data.id_user);
        localStorage.setItem("user",  JSON.stringify(res.data));
        console.log(localStorage.getItem("token"));
        console.log('');
        this.loadDataAfterSuccessLogin();
      })
      .catch(err => {
        if (err.response) {
          //TODO: show error response from server
          window.alert(err.response.data.message);
          console.log(err.response.data.message);
          this.setState({...this.state,error: err.response.data.message})
        } else if (err.request) {
          //TODO: msg internet connection..
          window.alert("Internet connection failed.");
        } else {
          //TODO: rly dont know what error can happend here but can happend :D
          window.alert("Something went wrong with login");
      }});
      event.preventDefault();
  };

  loadDataAfterSuccessLogin = () => {
    this.setState({
      ...this.state,
      islogged: true
    });
    window.location.reload();
  }

  render() {  
    if ( localStorage.getItem("token") ) {
      return <Redirect to="/" />
    }

    return (
      <Container component="main" maxWidth="xs" style={{"textAlign" : "center", "paddingTop" : "80px"}}>
        <CssBaseline />
        <div className={this.state.classes.paper} >
          <img src={logo} alt="logo" className={this.state.classes.img} style={{"width" : "10%"}} />
          <Typography component="h1" variant="h5" >
            SkautskÃ½ e-voting
          </Typography>
          <form className={this.state.classes.form} noValidate onSubmit={this.login}>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="login"
              label="EmailovÃ¡ adresa"
              name="login"
              autoComplete="email"
              autoFocus

              onChange={this.handleFormChange}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Heslo"
              type="password"
              id="password"
              autoComplete="current-password"

              onChange={this.handleFormChange}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={this.state.classes.submit}

              style={{"marginTop" : "30px"}}
            >
              PrihlÃ¡siÅ¥ sa
            </Button>

          </form>
        </div>
        <Box mt={8}>
          <Copyright />
        </Box>
      </Container>
    );}

}


export default LoginClass;
