declare type FocusStateType = {
    isFocus: boolean;
    focusIndex: number;
};
declare type Action = {
    type: 'setFocusIndex';
    focusIndex: number;
} | {
    type: 'toggleFocus';
    isFocus: boolean;
};
declare const initialState: FocusStateType;
declare const focusReducer: (state: FocusStateType, action: Action) => FocusStateType;
export { initialState, focusReducer };
